UPDATE SRV_QNA_BLT_EDN_D
SET       ANSWR_CNT = decode(NVL(#{updReplyCnt}, 0), '+1', NVL(ANSWR_CNT,0) + 1, '-1', NVL(ANSWR_CNT,0) - 1, NVL(ANSWR_CNT,0))
		   , BUYER_ANSWR_CNT = decode(NVL(#{updBuyerReplyCnt}, 0), '+1', NVL(BUYER_ANSWR_CNT,0) + 1, '-1', NVL(BUYER_ANSWR_CNT,0) - 1, NVL(BUYER_ANSWR_CNT,0))
		   , MODR_ID = #{modrId}
		   , MOD_DTM = #{modDtm}
		   , IF_SEQ = #{ifSeq}
		   , IF_YN = #{ifFlg}
		   , IF_DTM = #{ifDtm}
		   , IF_TYP_CD = #{ifTypCd}
		   , IF_SND_SYS_ID = #{ifSndSysId}
		   , IF_RCV_SYS_ID = #{ifRcvSysId}
		   , OWN_SYS_ID = #{ownSysId}
WHERE  BLT_THG_NO = #{upperPrdInqryNo}

